import React from 'react';
import {CopyToClipboard} from 'react-copy-to-clipboard';

// Copy button

function Copy()
{
    state = {
        value: '',
        copied: false,
      };

    return (
        <CopyToClipboard text='{this.state.value}'
          onCopy={() => this.setState({copied: true})}>
          <button>Copy to clipboard with button</button>
        </CopyToClipboard>
        );
}

export default Copy;
