import React, {useEffect, useState} from 'react';
import './Item.css';


function Item(props)
{
    const [count, setCount] = useState(Number(!props.count ? 1 : Number(props.count)));
    const [completed, setCompleted] = useState(false);

    const addCount = () => {
        setCount(prevCount => prevCount + 1);
    }

    const subtractCount = () => {
        setCount(prevCount => prevCount - 1);
    }

    return (
        <div className="item-container">
        <div className='item-body'>
            <div className='item-name-description'>
                <h2 className='item-name' style={{
  textDecoration: completed ? 'line-through' : 'none'
}}>{props.name}</h2>
                {props.description && <p className='item-description' style={{
  textDecoration: completed ? 'line-through' : 'none'
}}>{props.description}</p>}
            </div>
            <p className='item-count' style={{
  textDecoration: completed ? 'line-through' : 'none'
}}>{count}</p>
        </div>
        <div className='item-count-manipulator'>
            <button type='button' className='item-add' onClick={addCount}>+</button>
            <button type='button' className='item-subtract' onClick={subtractCount} disabled={count === 1 || !completed}>-</button>
        </div>
        </div>
    );
}

export default Item;
