import React, {useState} from 'react';
import './Item.css';


function Item(props)
{
    const [count, setCount] = useState(props.count);

    const addCount = () => {
        setCount(prevCount => prevCount + 1);
    }

    const subtractCount = () => {
        setCount(prevCount => prevCount - 1);
    }

    return (
        <div className="item-container">
        <div className='item-body'>
            <div className='item-name-description'>
                <h2 className='item-name'>{props.name}</h2>
                {props.description && <p className='item-description'>{props.description}</p>}
            </div>
            {!count ? <p className='item-count'>1</p> : <p className='item-count'>{count}</p>}
        </div>
        <div className='item-count-manipulator'>
            <button type='button' className='item-add' onClick={addCount}>+</button>
            <button type='button' className='item-subtract' onClick={subtractCount}>-</button>
        </div>
        </div>
    );
}

export default Item;
