import React, { useState } from "react";
import "./PromptBox.css";

function PromptBox() {
  // box to take in prompts with plus next to it (submit button)

  const [text, setText] = useState('');

  const endpoint = 'http://127.0.0.1:8000/add/';

  const handleTextChange = (e) => {
    setText(e.target.value);
  }

    const handleKeyPress = async () => {
        if (event.key === 'Enter') {
        try {
            const response = await fetch(endpoint+encodeURIComponent(text));
            const data = await response.json();
        } catch (error) {
            console.error("Error sending data:", error);
        }
    }
    };

  return (
    <form>
      <input className="promptBox-inputForm" placeholder="Add Item(s) " onChange={handleTextChange} onKeyPress={handleKeyPress}></input>
    </form>
  );
}

export default PromptBox;
