import React, {useState} from 'react';
import './Item.css';


function Item(props)
{
    // Item contains
    // checkbox | Item name | quantity / +/-
    // Description

    // if component clicked, mark as done/checked off

    return (
        <div className="item-container">
        <div className='item-body'>
            <div className='item-name-description'>
                <h2 className='item-name'>{props.name}</h2>
                {props.description && <p className='item-description'>{props.description}</p>}
            </div>
            <p className='item-count'>{props.count}</p>
        </div>
        <div className='item-count-manipulator'>
            <button type='button' className='item-add'>+</button>
            <button type='button' className='item-subtract'>-</button>
        </div>
        </div>
    );
}

export default Item;
