import logo from "./logo.svg";
import "./App.css";
import Item from "./components/Item.jsx";
import PromptBox from "./components/PromptBox.jsx";
import List from "./components/List.jsx";

function App() {
  const depts = [
    {
      "name": "Dairy",
      "items": [
        { "name": "Milk", "description": "Red Cap Milk, Check for Expiry", "count": "2" },
        { "name": "Butter", "description": "Unsalted, Preferred Brand", "count": "1" },
        { "name": "Cheese", "description": "Cheddar, Block", "count": "2" },
        { "name": "Yogurt", "description": "Greek, Plain", "count": "5" },
        { "name": "Eggs", "description": "Free-Range, Large", "count": "1 dozen" },
        { "name": "Sour Cream", "description": "Full Fat", "count": "1" },
        { "name": "Cottage Cheese", "description": "Small Curd", "count": "1" }
      ]
    },
    {
      "name": "Produce",
      "items": [
        { "name": "Tomato", "description": "Get Ripe Ones", "count": "12" },
        { "name": "Potatoes", "description": "Small Baby, in a Bag", "count": "1" },
        { "name": "Apples", "description": "Honeycrisp", "count": "6" },
        { "name": "Bananas", "description": "Slightly Green", "count": "1" },
        { "name": "Lettuce", "description": "Romaine Hearts", "count": "2" },
        { "name": "Carrots", "description": "Organic, Bagged", "count": "1" },
        { "name": "Cucumbers", "description": "English", "count": "3" },
        { "name": "Oranges", "description": "Navel", "count": "4" }
      ]
    },
    {
      "name": "Bakery",
      "items": [
        { "name": "Bread", "description": "Whole Wheat, Sliced", "count": "2" },
        { "name": "Bagels", "description": "Cinnamon Raisin", "count": "6" },
        { "name": "Croissants", "description": "Butter", "count": "6" },
        { "name": "Muffins", "description": "Blueberry", "count": "4" }
      ]
    },
    {
      "name": "Meat and Seafood",
      "items": [
        { "name": "Chicken Breast", "description": "Boneless, Skinless", "count": "2" },
        { "name": "Ground Beef", "description": "85% Lean", "count": "1" },
        { "name": "Salmon Fillets", "description": "Wild Caught", "count": "2" },
        { "name": "Shrimp", "description": "Large, Peeled and Deveined", "count": "1" },
        { "name": "Pork Chops", "description": "Bone-in", "count": "4" }
      ]
    },
    {
      "name": "Pantry",
      "items": [
        { "name": "Pasta", "description": "Spaghetti", "count": "2 packages" },
        { "name": "Rice", "description": "Basmati, White", "count": "1 bag" },
        { "name": "Canned Tomatoes", "description": "Diced", "count": "4 cans" },
        { "name": "Olive Oil", "description": "Extra Virgin", "count": "1 bottle" },
        { "name": "Peanut Butter", "description": "Creamy", "count": "1 jar" },
        { "name": "Coffee", "description": "Whole Bean, Dark Roast", "count": "1 bag" },
        { "name": "Cereal", "description": "Whole Grain, Preferred Brand", "count": "2 boxes" },
        { "name": "Granola Bars", "description": "Assorted Flavors", "count": "1 box" }
      ]
    }
  ]
  ;

  return (
    <div className="App">
      <List departments={depts} />
      <PromptBox />
    </div>
  );
}

export default App;
